local sx, sy = guiGetScreenSize()local phase = 0local c = trueaddEventHandler("onClientRender", root,	function()		if c then			phase = phase - 0.5			if phase <= 0 then				phase = 0				c = false			end			else			phase = phase + 0.5			if phase >= 255 then				phase = 255				c = true			end		end		local t = "Escribir Algo."		dxDrawText("Escribir info aqui", sx / 2, 0, sx / 2, sy, tocolor(255, 255, 255, 255), 1, "default-bold", "center", "top")		dxDrawText(t, sx / 2, 15, sx / 2, sy, tocolor(255, 0, 0, phase), 1, "default-bold", "center", "top")	end)